SELECT a.idestado, a.estado, a.ranking, a.segmento_gestion, 
 a.recuperable, a.objetivo_bono, a.liquidacion, a.comision, a.bonos, a.salida_anticipada, a.condicionamiento, a.proceso_alta, a.fecha_actualizado, a.status
FROM sales_management.dim_states a 
WHERE idcampa = {{lst_campas.triggeredItem.idcampa}}
AND status = 1;
