srcDoc.css:
.custom-modal {
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
}

.slider-container {
  position: relative;
  width: 90%;
  max-width: 900px;
  height: 500px;
  overflow: hidden;
  border-radius: 12px;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.2);
  background: #fff;
}

.slide {
  display: none;
  width: 100%;
  height: 100%;
}

.slide.active {
  display: flex;
  justify-content: center;
  align-items: center;
}

.zoomable {
  max-width: 100%;
  max-height: 100%;
  transition: transform 0.3s ease;
}

/* Navigation buttons */
.prev, .next {
  cursor: pointer;
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  background-color: rgba(0, 0, 0, 0.5);
  color: #fff;
  border: none;
  font-size: 24px;
  padding: 10px 16px;
  border-radius: 50%;
  user-select: none;
  transition: background 0.3s;
}

.prev:hover, .next:hover {
  background-color: rgba(0, 0, 0, 0.8);
}

.prev {
  left: 15px;
}

.next {
  right: 15px;
}

/* Dots */
.dots {
  text-align: center;
  position: absolute;
  bottom: 15px;
  width: 100%;
}

.dot {
  cursor: pointer;
  height: 12px;
  width: 12px;
  margin: 0 6px;
  background-color: #bbb;
  border-radius: 50%;
  display: inline-block;
  transition: background-color 0.3s;
}

.dot.active, .dot:hover {
  background-color: #333;
}

/* Zoom controls */
.zoom-controls {
  position: absolute;
  top: 15px;
  right: 15px;
  display: flex;
  gap: 6px;
}

.zoom-controls button {
  background: rgba(0, 0, 0, 0.6);
  color: white;
  border: none;
  padding: 6px 10px;
  font-size: 16px;
  border-radius: 6px;
  cursor: pointer;
  transition: background 0.3s;
}

.zoom-controls button:hover {
  background: rgba(0, 0, 0, 0.8);
}

.zoomable {
  max-width: 100%;
  max-height: 100%;
  transition: transform 0.2s ease;
  cursor: grab;
}

.zoomable:active {
  cursor: grabbing;
}

srcDoc.js:
let currentSlide = 0;
const slides = document.querySelectorAll(".slide");
const dots = document.querySelectorAll(".dot");
const prevBtn = document.querySelector(".prev");
const nextBtn = document.querySelector(".next");
let zoomLevel = 1;
let panX = 0,
  panY = 0;
let isPanning = false;
let startX, startY;
function showSlide(index) {
  if (index >= slides.length) currentSlide = 0;
  if (index < 0) currentSlide = slides.length - 1;
  slides.forEach((slide, i) => {
    slide.classList.remove("active");
    dots[i].classList.remove("active");
  });
  slides[currentSlide].classList.add("active");
  dots[currentSlide].classList.add("active");
  resetZoom(); // reset zoom when changing slide
}
function applyTransform() {
  const activeImg = slides[currentSlide].querySelector(".zoomable");
  activeImg.style.transform = `translate(${panX}px, ${panY}px) scale(${zoomLevel})`;
}
function resetZoom() {
  zoomLevel = 1;
  panX = 0;
  panY = 0;
  applyTransform();
}

// Navigation
nextBtn.addEventListener("click", () => {
  currentSlide++;
  showSlide(currentSlide);
});
prevBtn.addEventListener("click", () => {
  currentSlide--;
  showSlide(currentSlide);
});

// Dot navigation
dots.forEach((dot, i) => {
  dot.addEventListener("click", () => {
    currentSlide = i;
    showSlide(currentSlide);
  });
});

// Zoom controls
document.getElementById("zoomIn").addEventListener("click", () => {
  zoomLevel += 0.2;
  applyTransform();
});
document.getElementById("zoomOut").addEventListener("click", () => {
  zoomLevel = Math.max(0.5, zoomLevel - 0.2);
  applyTransform();
});
document.getElementById("resetZoom").addEventListener("click", resetZoom);

// Pan controls
slides.forEach(slide => {
  const img = slide.querySelector(".zoomable");
  img.addEventListener("mousedown", e => {
    if (zoomLevel <= 1) return; // no pan if not zoomed
    isPanning = true;
    startX = e.clientX - panX;
    startY = e.clientY - panY;
  });
  img.addEventListener("mousemove", e => {
    if (!isPanning) return;
    panX = e.clientX - startX;
    panY = e.clientY - startY;
    applyTransform();
  });
  img.addEventListener("mouseup", () => isPanning = false);
  img.addEventListener("mouseleave", () => isPanning = false);
});

// Init
showSlide(currentSlide);

